mapper.select:

SELECT SUBSTR(cast(sig_cd as char(20)), 1, 5) AS VALUE,
       sig_kor_nm AS NAME
FROM   PUBLIC.gj_sgg;

SELECT NUM ,ITPT_ID ,TO_DATE(SUBSTR(ITPT_DT,1,8), 'YYYYMMDD') ,ITPT_STATE_CD ,ITPT_TYPE_CD ,CONCAT(ITPT_SGG_NM || ' ', ITPT_EMD_NM || ' ', ITPT_RI_NM || ' ', ITPT_JIBUN || ' ') AS ITPT_ADDRESS ,ITPT_PERSON ,TO_CODE('TBL_INTERPRETATION', 'GB', ITPT_RESULT_GB) AS ITPT_GB_CD ,TO_CODE('TBL_INTERPRETATION', 'BF', ITPT_RESULT_BF) AS ITPT_BF_CD ,ITPT_OPINION FROM (SELECT ( ROW_NUMBER() OVER() ) AS NUM, TEMP.* ,TO_CODE('TBL_INTERPRETATION', 'STATE', ITPT_STATE) AS ITPT_STATE_CD ,TO_CODE('TBL_INTERPRETATION', 'TYPE', ITPT_TYPE) AS ITPT_TYPE_CD FROM (SELECT * FROM PUBLIC.TBL_INTERPRETATION
ITPT_TYPE = #{checkList.type} AND ITPT_RESULT_GB = #{checkList.searchGb}
AND ITPT_RESULT_BF = #{searchBf}
AND SUBSTR(ITPT_PNU, 1, 5) = #{nSggB}
AND SUBSTR(ITPT_PNU, 6, 3) = #{nEmdB}
AND SUBSTR(ITPT_PNU, 9, 2) = #{nRiB}
AND ITPT_STATE = #{searchState}
AND ITPT_STATE != #{searchStateN}
AND ITPT_STATE != #{searchStateN2}
AND SUBSTR(ITPT_PNU, 11, 1) = '2'
AND SUBSTR(ITPT_PNU, 11, 1) = '1'
AND SUBSTR(ITPT_PNU, 12, 4) = SUBSTR('0000'||#{searchCtt},char_length('0000'||#{searchCtt})-3)
AND SUBSTR(ITPT_PNU, 16, 4) = SUBSTR('0000'||#{searchCtt2},char_length('0000'||#{searchCtt2})-3)
AND TO_DATE(SUBSTR(ITPT_DT,1,8), 'YYYYMMDD') >= TO_DATE(#{nDateStd}, 'YYYY-MM-DD')
AND TO_DATE(SUBSTR(ITPT_DT,1,8), 'YYYYMMDD') <= TO_DATE(#{nDateEnd}, 'YYYY-MM-DD')
AND SUBSTRING(ITPT_ID,1,5) = (SELECT SUBSTRING(SGG_CD,1,5) FROM GJ_SGG WHERE SGG_NM = (SELECT TO_CODE('ORGAN', #{userOrgan}) AS SGG_NM FROM public.tbl_user_info LIMIT 1))
AND ITPT_TYPE != '0000'
ORDER BY ITPT_ID ASC ) TEMP)TEMP
